// src/components/ProjectsSection.js
import React from 'react';
import './ProjectsSection.css';

const projects = [
  {
    title: "AASA Customer Billing System",
    description: "A dynamic billing system where sellers can generate bills and buyers can log in to view their billing history securely. Designed for smooth transaction tracking and record management.",
    image: "/project1.jpg",
    tech: ["PHP", "HTML", "CSS", "MySQL"],
  },
  {
    title: "Intern Management System",
    description: "A full-stack platform with three dashboards: for admins, intern providers, and intern seekers. Providers post jobs, seekers apply, and admins manage the system. Ensures seamless internship coordination.",
    image: "/project2.jpg",
    tech: ["Ruby on Rails", "PostgreSQL", "HTML", "CSS", "Bootstrap, JavaScript"],
  },
  {
    title: "Task Management Tool",
    description: "Team collaboration platform with task tracking, Kanban board, and ClickUp-style interactions.",
    image: "/project3.jpg",
    tech: ["HTML", "CSS", "JavaScript"],
  },
];

const ProjectsSection = () => {
  return (
    <div className="projects-section" id="projects">
      <h2 className="projects-title">My Projects</h2>
      <div className="projects-grid">
        {projects.map((project, index) => (
          <div key={index} className="project-card">
            <img src={project.image} alt={project.title} className="project-img" />
            <div className="project-content">
              <h3>{project.title}</h3>
              <p>{project.description}</p>
              <div className="tech-tags">
                {project.tech.map((tag, i) => (
                  <span key={i} className="tech-badge">{tag}</span>
                ))}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProjectsSection;
